pragma solidity 0.8.30;

import { ISomniaEventHandler } from "./interfaces/ISomniaEventHandler.sol";
import { SomniaExtensions } from "./interfaces/ISomniaReactivityPrecompile.sol";
import { IERC165 } from "./interfaces/IERC165.sol";

/// @notice Abstract smart contract implementing native on-chain reactivity safely with reduced boilerplate
/// @dev The _onEvent virtual method must be overriden to define logic executed when subscription callbacks are invoked by the chain
/// @dev The contract is deemed upgrade safe due to the absense of storage variables
abstract contract SomniaEventHandler is IERC165, ISomniaEventHandler {

    /**
     * Errors
     */
    error OnlyReactivityPrecompile();

    /// @inheritdoc ISomniaEventHandler
    function onEvent(
        address emitter,
        bytes32[] calldata eventTopics,
        bytes calldata data
    ) external override {
        // Ensure only privileged execution is permitted since the receiving contract may execute its own side effects i.e. releasing prize money
        require(
            msg.sender == SomniaExtensions.SOMNIA_REACTIVITY_PRECOMPILE_ADDRESS,
            OnlyReactivityPrecompile()
        );
        _onEvent(emitter, eventTopics, data);
    }

    /**
     * @dev Implementing contract must override to define what logic it wants to execute for subscription callbacks
     * @param emitter Smart contract that emitted the EVM event log
     * @param eventTopics List of event topics associated with the event (event signature being first param)
     * @param data Event data for non-indexed event arguments. May be empty if event only has indexed params
     */
    function _onEvent(
        address emitter,
        bytes32[] calldata eventTopics,
        bytes calldata data
    ) internal virtual;

    /// @dev Allows the Somnia reactivity precompile to reason about support for reactivity subscriptions
    function supportsInterface(bytes4 interfaceId) external pure override returns (bool) {
        return type(IERC165).interfaceId == interfaceId 
            || type(ISomniaEventHandler).interfaceId == interfaceId;
    }
}

contracts/BrimdexParimutuelMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./BrimdexParimutuelToken.sol";

/// @title BrimdexParimutuelMarket
/// @notice Parimutuel betting market for crypto price ranges
/// @dev Users buy BOUND/BREAK tokens, winners split the pool at settlement
contract BrimdexParimutuelMarket is Ownable {
    using SafeERC20 for IERC20;

    // Market configuration
    struct MarketConfig {
        string name;               // Asset name (max 8 characters, e.g., "ETH", "BTC", "SOL")
        uint256 lowerBound;        // Lower price bound (6 decimals)
        uint256 upperBound;        // Upper price bound (6 decimals)
        uint256 expiryTimestamp;   // Market expiry time
        uint256 creationTimestamp; // When market was created
        uint256 startPrice;        // Price at market creation (6 decimals)
        bool initialized;          // Market initialized
        bool settled;              // Market settled
    }

    MarketConfig public marketConfig;

    // Collateral token (USDC)
    IERC20 public immutable collateralToken;

    // BOUND and BREAK tokens (ERC20)
    BrimdexParimutuelToken public immutable boundToken;
    BrimdexParimutuelToken public immutable breakToken;

    // Factory address (can initialize)
    address public immutable factory;

    // Pools (in USDC, 6 decimals)
    uint256 public boundPool;
    uint256 public breakPool;

    // Settlement state
    uint256 public redemptionRate; // For winning side (1e18 precision)
    bool public boundWins;

    // Fee: 2% (200 basis points)
    uint256 public constant FEE_BPS = 200;
    uint256 public constant BPS_DENOMINATOR = 10000;

    // Bootstrap amounts (6 decimals)
    uint256 public constant BOOTSTRAP_BOUND = 10_000_000; // $10
    uint256 public constant BOOTSTRAP_BREAK = 10_000_000; // $10

    // Events
    event MarketInitialized(
        string name,
        uint256 lowerBound,
        uint256 upperBound,
        uint256 expiryTimestamp
    );
    event BoundPurchased(address indexed buyer, uint256 amount, uint256 tokens, uint256 price);
    event BreakPurchased(address indexed buyer, uint256 amount, uint256 tokens, uint256 price);
    event MarketSettled(bool boundWins, uint256 totalPool, uint256 winnings);
    event TokensRedeemed(address indexed user, uint256 tokens, uint256 payout);

    constructor(
        address _collateralToken,
        address _boundToken,
        address _breakToken,
        address _owner,
        address _factory
    ) Ownable(_owner) {
        collateralToken = IERC20(_collateralToken);
        boundToken = BrimdexParimutuelToken(_boundToken);
        breakToken = BrimdexParimutuelToken(_breakToken);
        factory = _factory;
    }

    /// @notice Initialize the market
    function initialize(
        string memory _name,
        uint256 _lowerBound,
        uint256 _upperBound,
        uint256 _expiryTimestamp,
        uint256 _startPrice
    ) external {
        require(msg.sender == factory || msg.sender == owner(), "Not authorized");
        require(!marketConfig.initialized, "Already initialized");
        require(_expiryTimestamp > block.timestamp, "Invalid expiry");
        require(_lowerBound < _upperBound, "Invalid bounds");
        require(bytes(_name).length > 0 && bytes(_name).length <= 8, "Name must be 1-8 characters");

        marketConfig = MarketConfig({
            name: _name,
            lowerBound: _lowerBound,
            upperBound: _upperBound,
            expiryTimestamp: _expiryTimestamp,
            creationTimestamp: block.timestamp,
            startPrice: _startPrice,
            initialized: true,
            settled: false
        });

        // Bootstrap: mint initial tokens to burn address
        address burnAddress = address(0x000000000000000000000000000000000000dEaD);

        // Verify that bootstrap USDC was already sent to this contract by factory
        // Factory sends USDC before calling initialize, so we just check balance
        uint256 requiredBootstrap = BOOTSTRAP_BOUND + BOOTSTRAP_BREAK;
        require(
            collateralToken.balanceOf(address(this)) >= requiredBootstrap,
            "Insufficient bootstrap funds"
        );

        // Mint bootstrap tokens
        boundToken.mint(burnAddress, BOOTSTRAP_BOUND);
        breakToken.mint(burnAddress, BOOTSTRAP_BREAK);

        // Initialize pools
        boundPool = BOOTSTRAP_BOUND;
        breakPool = BOOTSTRAP_BREAK;

        emit MarketInitialized(_name, _lowerBound, _upperBound, _expiryTimestamp);
    }

    /// @notice Buy BOUND tokens
    /// @param amount Amount of USDC to spend (6 decimals)
    function buyBound(uint256 amount) external {
        require(marketConfig.initialized, "Not initialized");
        require(!marketConfig.settled, "Market settled");
        require(block.timestamp < marketConfig.expiryTimestamp, "Market expired");
        require(amount > 0, "Amount must be > 0");

        // Transfer USDC from user
        collateralToken.safeTransferFrom(msg.sender, address(this), amount);

        // Calculate current price
        uint256 totalPool = boundPool + breakPool;
        uint256 price = (boundPool * 1e18) / totalPool; // 18 decimals for precision

        // Calculate tokens to mint
        uint256 tokens = (amount * 1e18) / price; // 18 decimals
        tokens = tokens / 1e12; // Convert to 6 decimals (token decimals)

        // Mint tokens to user
        boundToken.mint(msg.sender, tokens);

        // Update pool
        boundPool += amount;

        emit BoundPurchased(msg.sender, amount, tokens, price);
    }

    /// @notice Buy BREAK tokens
    /// @param amount Amount of USDC to spend (6 decimals)
    function buyBreak(uint256 amount) external {
        require(marketConfig.initialized, "Not initialized");
        require(!marketConfig.settled, "Market settled");
        require(block.timestamp < marketConfig.expiryTimestamp, "Market expired");
        require(amount > 0, "Amount must be > 0");

        // Transfer USDC from user
        collateralToken.safeTransferFrom(msg.sender, address(this), amount);

        // Calculate current price
        uint256 totalPool = boundPool + breakPool;
        uint256 price = (breakPool * 1e18) / totalPool; // 18 decimals for precision

        // Calculate tokens to mint
        uint256 tokens = (amount * 1e18) / price; // 18 decimals
        tokens = tokens / 1e12; // Convert to 6 decimals (token decimals)

        // Mint tokens to user
        breakToken.mint(msg.sender, tokens);

        // Update pool
        breakPool += amount;

        emit BreakPurchased(msg.sender, amount, tokens, price);
    }

    /// @notice Settle the market
    /// @param finalPrice Final price from oracle (6 decimals)
    function settle(uint256 finalPrice) external onlyOwner {
        require(marketConfig.initialized, "Not initialized");
        require(!marketConfig.settled, "Already settled");
        require(block.timestamp >= marketConfig.expiryTimestamp, "Not expired");

        // Determine winner
        boundWins = (finalPrice >= marketConfig.lowerBound && finalPrice <= marketConfig.upperBound);
        marketConfig.settled = true;

        // Calculate winnings (after 2% fee)
        uint256 totalPool = boundPool + breakPool;
        uint256 fee = (totalPool * FEE_BPS) / BPS_DENOMINATOR;
        uint256 winnings = totalPool - fee;

        // Calculate redemption rate for winning side
        if (boundWins) {
            uint256 totalBoundSupply = boundToken.totalSupply();
            if (totalBoundSupply > 0) {
                redemptionRate = (winnings * 1e18) / totalBoundSupply;
            }
        } else {
            uint256 totalBreakSupply = breakToken.totalSupply();
            if (totalBreakSupply > 0) {
                redemptionRate = (winnings * 1e18) / totalBreakSupply;
            }
        }

        // Transfer fee to owner
        if (fee > 0) {
            collateralToken.safeTransfer(owner(), fee);
        }

        emit MarketSettled(boundWins, totalPool, winnings);
    }

    /// @notice Redeem winning tokens for USDC
    /// @param isBound True if redeeming BOUND tokens, false for BREAK
    /// @param amount Amount of tokens to redeem
    function redeem(bool isBound, uint256 amount) external {
        require(marketConfig.settled, "Not settled");
        require(redemptionRate > 0, "No redemption rate set");
        require(amount > 0, "Amount must be > 0");

        uint256 payout;

        if (isBound) {
            require(boundWins, "BOUND did not win");
            require(boundToken.balanceOf(msg.sender) >= amount, "Insufficient BOUND");

            // Burn tokens
            boundToken.burnFrom(msg.sender, amount);

            // Calculate payout
            payout = (amount * redemptionRate) / 1e18;
        } else {
            require(!boundWins, "BREAK did not win");
            require(breakToken.balanceOf(msg.sender) >= amount, "Insufficient BREAK");

            // Burn tokens
            breakToken.burnFrom(msg.sender, amount);

            // Calculate payout
            payout = (amount * redemptionRate) / 1e18;
        }

        require(payout > 0, "No payout");

        // Transfer USDC to user
        collateralToken.safeTransfer(msg.sender, payout);

        emit TokensRedeemed(msg.sender, amount, payout);
    }

    /// @notice Get current BOUND price (1e18 precision)
    function getBoundPrice() external view returns (uint256) {
        uint256 totalPool = boundPool + breakPool;
        if (totalPool == 0) return 5e17; // 0.5 if empty
        return (boundPool * 1e18) / totalPool;
    }

    /// @notice Get current BREAK price (1e18 precision)
    function getBreakPrice() external view returns (uint256) {
        uint256 totalPool = boundPool + breakPool;
        if (totalPool == 0) return 5e17; // 0.5 if empty
        return (breakPool * 1e18) / totalPool;
    }

    /// @notice Get estimated tokens for amount
    function getEstimatedTokens(bool isBound, uint256 amount) external view returns (uint256) {
        uint256 totalPool = boundPool + breakPool;
        if (totalPool == 0) {
            // If empty, assume 0.5 price
            return amount * 2; // 1 / 0.5 = 2
        }

        uint256 price = isBound 
            ? (boundPool * 1e18) / totalPool
            : (breakPool * 1e18) / totalPool;

        return (amount * 1e18) / price / 1e12; // Convert to 6 decimals
    }

    /// @notice Get estimated payout if side wins
    function getEstimatedPayout(bool isBound, uint256 tokens) external view returns (uint256) {
        if (marketConfig.settled) {
            // Market settled, return actual redemption
            if ((isBound && boundWins) || (!isBound && !boundWins)) {
                return (tokens * redemptionRate) / 1e18;
            }
            return 0;
        }

        // Estimate based on current pools
        uint256 totalPool = boundPool + breakPool;
        if (totalPool == 0) return 0;

        uint256 winnings = (totalPool * (BPS_DENOMINATOR - FEE_BPS)) / BPS_DENOMINATOR;

        if (isBound) {
            uint256 totalBoundSupply = boundToken.totalSupply();
            if (totalBoundSupply == 0) return 0;
            return (tokens * winnings) / totalBoundSupply;
        } else {
            uint256 totalBreakSupply = breakToken.totalSupply();
            if (totalBreakSupply == 0) return 0;
            return (tokens * winnings) / totalBreakSupply;
        }
    }
}

contracts/BrimdexParimutuelMarketFactory.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./BrimdexParimutuelMarket.sol";
import "./BrimdexParimutuelToken.sol";

/// @title BrimdexParimutuelMarketFactory
/// @notice Factory for creating parimutuel markets
contract BrimdexParimutuelMarketFactory is Ownable {
    using SafeERC20 for IERC20;
    address public immutable collateralToken; // USDC
    address[] public markets;

    mapping(address => bool) public isMarket;
    mapping(address => address) public marketToBoundToken;
    mapping(address => address) public marketToBreakToken;

    // Track active markets by name+timeframe to prevent duplicates
    // Key: keccak256(abi.encodePacked(name, timeframeDuration))
    // Value: market address (address(0) if no active market)
    mapping(bytes32 => address) public activeMarkets;

    event MarketCreated(
        address indexed market,
        address indexed boundToken,
        address indexed breakToken,
        string name,
        uint256 lowerBound,
        uint256 upperBound,
        uint256 expiryTimestamp
    );

    constructor(address _collateralToken, address _owner) Ownable(_owner) {
        collateralToken = _collateralToken;
    }

    /// @notice Create a new parimutuel market
    /// @param name Asset name (max 8 characters, e.g., "ETH", "BTC", "SOL")
    /// @param timeframeDuration Duration in seconds (e.g., 86400 for 24h, 604800 for 7d)
    /// @param startPrice Price at market creation (6 decimals) - used for display
    function createMarket(
        string memory name,
        uint256 lowerBound,
        uint256 upperBound,
        uint256 expiryTimestamp,
        uint256 timeframeDuration,
        uint256 startPrice,
        string memory boundTokenName,
        string memory boundTokenSymbol,
        string memory breakTokenName,
        string memory breakTokenSymbol
    ) external onlyOwner returns (address market, address boundToken, address breakToken) {
        require(bytes(name).length > 0 && bytes(name).length <= 8, "Name must be 1-8 characters");

        // Check for duplicate active markets (only for short-term markets ≤ 24h)
        if (timeframeDuration <= 86400) { // 24 hours in seconds
            bytes32 marketKey = keccak256(abi.encodePacked(name, timeframeDuration));
            address existingMarket = activeMarkets[marketKey];

            if (existingMarket != address(0)) {
                // Check if existing market is still active (not settled and not expired)
                BrimdexParimutuelMarket existing = BrimdexParimutuelMarket(existingMarket);

                // Access struct fields individually (public struct returns tuple)
                // Order: name, lowerBound, upperBound, expiryTimestamp, creationTimestamp, startPrice, initialized, settled
                (,,,uint256 existingExpiry,,,bool _initialized,bool existingSettled) = existing.marketConfig();

                // If not settled and not expired, reject creation
                if (!existingSettled && existingExpiry > block.timestamp) {
                    revert("Active market already exists for this name+timeframe");
                }
            }

            // Mark this as the active market
            activeMarkets[marketKey] = address(0); // Will be set after deployment
        }
        // Deploy tokens
        boundToken = address(new BrimdexParimutuelToken(boundTokenName, boundTokenSymbol, address(this)));
        breakToken = address(new BrimdexParimutuelToken(breakTokenName, breakTokenSymbol, address(this)));

        // Deploy market
        market = address(new BrimdexParimutuelMarket(
            collateralToken,
            boundToken,
            breakToken,
            msg.sender,
            address(this)
        ));

        // Transfer token ownership to market
        BrimdexParimutuelToken(boundToken).transferOwnership(market);
        BrimdexParimutuelToken(breakToken).transferOwnership(market);

        // Pull bootstrap USDC from owner and send to market
        // Owner must have approved this factory to spend USDC
        uint256 bootstrapAmount = 20_000_000; // $20 (10 + 10) in 6 decimals
        IERC20(collateralToken).safeTransferFrom(msg.sender, market, bootstrapAmount);

        // Initialize market (will use USDC already sent to it)
        BrimdexParimutuelMarket(market).initialize(
            name,
            lowerBound,
            upperBound,
            expiryTimestamp,
            startPrice
        );

        // Register
        markets.push(market);
        isMarket[market] = true;
        marketToBoundToken[market] = boundToken;
        marketToBreakToken[market] = breakToken;

        // Update active market mapping for short-term markets
        if (timeframeDuration <= 86400) {
            bytes32 marketKey = keccak256(abi.encodePacked(name, timeframeDuration));
            activeMarkets[marketKey] = market;
        }

        emit MarketCreated(
            market,
            boundToken,
            breakToken,
            name,
            lowerBound,
            upperBound,
            expiryTimestamp
        );
    }

    /// @notice Get all markets
    function getAllMarkets() external view returns (address[] memory) {
        return markets;
    }

    /// @notice Get market count
    function getMarketCount() external view returns (uint256) {
        return markets.length;
    }
}

contracts/BrimdexParimutuelOrderBook.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {OrderBookLinkedList} from "./libraries/OrderBookLinkedList.sol";
import {OrderPriceVolumeSet} from "./libraries/OrderPriceVolumeSet.sol";
import "./BrimdexParimutuelMarketFactory.sol";

/// @title BrimdexParimutuelOrderBook
/// @notice Simple on-chain orderbook for trading BOUND/BREAK tokens
/// @dev No minting - only transfers existing tokens between users
contract BrimdexParimutuelOrderBook is Ownable {
    using SafeERC20 for IERC20;
    using OrderBookLinkedList for OrderBookLinkedList.LinkedList;
    using OrderPriceVolumeSet for OrderPriceVolumeSet.OPVset;

    IERC20 public immutable collateralToken; // USDC
    BrimdexParimutuelMarketFactory public immutable marketFactory;

    uint16 public feeRate; // Fee in basis points (e.g., 50 = 0.5%)
    uint256 public accumulatedFee;

    // Order book: market => token => price => orders
    mapping(address => mapping(address => mapping(uint256 => OrderBookLinkedList.LinkedList)))
        public sellOrderBook;
    mapping(address => mapping(address => mapping(uint256 => OrderBookLinkedList.LinkedList)))
        public buyOrderBook;

    // User orders: market => token => user => [orders]
    mapping(address => mapping(address => OrderPriceVolumeSet.OPVset)) private _sellOrders;
    mapping(address => mapping(address => OrderPriceVolumeSet.OPVset)) private _buyOrders;

    // Active prices for efficient matching: market => token => prices[]
    mapping(address => mapping(address => uint256[])) private _sellPrices;
    mapping(address => mapping(address => uint256[])) private _buyPrices;
    mapping(address => mapping(address => mapping(uint256 => bool))) private _activePrices;

    // Events
    event OrderPlaced(
        address indexed market,
        address indexed token,
        address indexed user,
        uint256 price,
        uint256 amount,
        bytes32 orderId,
        bool isBuy
    );

    event OrderMatched(
        address indexed market,
        address indexed token,
        address indexed maker,
        address taker,
        uint256 price,
        uint256 amount
    );

    event OrderCancelled(
        address indexed market,
        address indexed token,
        address indexed user,
        uint256 price,
        bytes32 orderId
    );

    constructor(
        address _collateralToken,
        address _marketFactory,
        address _owner
    ) Ownable(_owner) {
        collateralToken = IERC20(_collateralToken);
        marketFactory = BrimdexParimutuelMarketFactory(_marketFactory);
        feeRate = 50; // 0.5% fee
    }

    /// @notice Set trading fee rate
    function setFeeRate(uint16 _feeRate) external onlyOwner {
        require(_feeRate <= 1000, "Fee too high");
        feeRate = _feeRate;
    }

    /// @notice Place a sell order
    /// @param market Market contract address
    /// @param token BOUND or BREAK token address
    /// @param price Price per token in USDC (6 decimals)
    /// @param amount Amount of tokens to sell
    function placeSellOrder(
        address market,
        address token,
        uint256 price,
        uint256 amount
    ) external returns (bytes32 orderId) {
        require(marketFactory.isMarket(market), "Invalid market");
        require(
            token == marketFactory.marketToBoundToken(market) ||
            token == marketFactory.marketToBreakToken(market),
            "Invalid token for market"
        );
        require(price > 0, "Price must be > 0");
        require(amount > 0, "Amount must be > 0");

        IERC20 tokenContract = IERC20(token);

        // Transfer tokens from user to this contract
        tokenContract.safeTransferFrom(msg.sender, address(this), amount);

        // Match against existing buy orders
        uint256 remainingAmount = _matchSellOrder(market, token, price, amount);

        // If not fully filled, add to order book
        if (remainingAmount > 0) {
            if (sellOrderBook[market][token][price].length == 0) {
                orderId = sellOrderBook[market][token][price].initHead(msg.sender, remainingAmount);
                _addSellPrice(market, token, price);
            } else {
                orderId = sellOrderBook[market][token][price].addNode(msg.sender, remainingAmount);
            }

            _sellOrders[market][token]._add(msg.sender, orderId, price, remainingAmount);
            emit OrderPlaced(market, token, msg.sender, price, remainingAmount, orderId, false);
        }
    }

    /// @notice Place a buy order
    /// @param market Market contract address
    /// @param token BOUND or BREAK token address
    /// @param price Price per token in USDC (6 decimals)
    /// @param amount Amount of tokens to buy
    function placeBuyOrder(
        address market,
        address token,
        uint256 price,
        uint256 amount
    ) external returns (bytes32 orderId) {
        require(marketFactory.isMarket(market), "Invalid market");
        require(
            token == marketFactory.marketToBoundToken(market) ||
            token == marketFactory.marketToBreakToken(market),
            "Invalid token for market"
        );
        require(price > 0, "Price must be > 0");
        require(amount > 0, "Amount must be > 0");

        // Calculate USDC needed
        uint256 totalUsdc = (amount * price) / 1e6;
        uint256 fee = (totalUsdc * feeRate) / 10000;
        accumulatedFee += fee;
        uint256 netUsdc = totalUsdc + fee;

        // Transfer USDC from user
        collateralToken.safeTransferFrom(msg.sender, address(this), netUsdc);

        // Match against existing sell orders
        uint256 remainingAmount = _matchBuyOrder(market, token, price, amount);

        // If not fully filled, add to order book
        if (remainingAmount > 0) {
            uint256 remainingUsdc = (remainingAmount * price) / 1e6;

            if (buyOrderBook[market][token][price].length == 0) {
                orderId = buyOrderBook[market][token][price].initHead(msg.sender, remainingUsdc);
                _addBuyPrice(market, token, price);
            } else {
                orderId = buyOrderBook[market][token][price].addNode(msg.sender, remainingUsdc);
            }

            _buyOrders[market][token]._add(msg.sender, orderId, price, remainingUsdc);
            emit OrderPlaced(market, token, msg.sender, price, remainingAmount, orderId, true);
        }
    }

    /// @notice Cancel a sell order
    function cancelSellOrder(
        address market,
        address token,
        uint256 price,
        bytes32 orderId
    ) external {
        OrderBookLinkedList.Order memory o = sellOrderBook[market][token][price]
            .nodes[orderId]
            .order;
        require(msg.sender == o.seller, "Not order owner");

        IERC20(token).safeTransfer(msg.sender, o.amount);

        sellOrderBook[market][token][price].deleteNode(orderId);
        _sellOrders[market][token]._remove(msg.sender, orderId);
        _removePrice(market, token, price, true);

        emit OrderCancelled(market, token, msg.sender, price, orderId);
    }

    /// @notice Cancel a buy order
    function cancelBuyOrder(
        address market,
        address token,
        uint256 price,
        bytes32 orderId
    ) external {
        OrderBookLinkedList.Order memory o = buyOrderBook[market][token][price]
            .nodes[orderId]
            .order;
        require(msg.sender == o.seller, "Not order owner");

        collateralToken.safeTransfer(msg.sender, o.amount);

        buyOrderBook[market][token][price].deleteNode(orderId);
        _buyOrders[market][token]._remove(msg.sender, orderId);
        _removePrice(market, token, price, false);

        emit OrderCancelled(market, token, msg.sender, price, orderId);
    }

    /// @notice Internal: Match sell order against buy orders
    function _matchSellOrder(
        address market,
        address token,
        uint256 price,
        uint256 sellAmount
    ) internal returns (uint256 remainingAmount) {
        remainingAmount = sellAmount;
        uint256 len = buyOrderBook[market][token][price].length;

        IERC20 tokenContract = IERC20(token);

        for (uint256 i = 0; i < len && remainingAmount > 0; i++) {
            bytes32 head_ = buyOrderBook[market][token][price].head;
            uint256 buyUsdc = buyOrderBook[market][token][price].nodes[head_].order.amount;
            uint256 buyAmount = (buyUsdc * 1e6) / price;

            OrderBookLinkedList.Order memory buyOrder = buyOrderBook[market][token][price]
                .nodes[head_]
                .order;

            if (remainingAmount >= buyAmount) {
                // Full match
                buyOrderBook[market][token][price].popHead();
                _buyOrders[market][token]._remove(buyOrder.seller, head_);
                _removePrice(market, token, price, false);

                tokenContract.safeTransfer(buyOrder.seller, buyAmount);
                collateralToken.safeTransfer(msg.sender, buyUsdc);

                emit OrderMatched(market, token, buyOrder.seller, msg.sender, price, buyAmount);

                remainingAmount -= buyAmount;
            } else {
                // Partial match
                uint256 matchedUsdc = (remainingAmount * price) / 1e6;

                buyOrderBook[market][token][price].nodes[head_].order.amount -= matchedUsdc;
                _buyOrders[market][token]._subVolume(buyOrder.seller, head_, matchedUsdc);

                tokenContract.safeTransfer(buyOrder.seller, remainingAmount);
                collateralToken.safeTransfer(msg.sender, matchedUsdc);

                emit OrderMatched(market, token, buyOrder.seller, msg.sender, price, remainingAmount);

                remainingAmount = 0;
            }
        }
    }

    /// @notice Internal: Match buy order against sell orders
    function _matchBuyOrder(
        address market,
        address token,
        uint256 price,
        uint256 buyAmount
    ) internal returns (uint256 remainingAmount) {
        remainingAmount = buyAmount;
        uint256 len = sellOrderBook[market][token][price].length;

        IERC20 tokenContract = IERC20(token);

        for (uint256 i = 0; i < len && remainingAmount > 0; i++) {
            bytes32 head_ = sellOrderBook[market][token][price].head;
            uint256 sellAmount = sellOrderBook[market][token][price].nodes[head_].order.amount;

            OrderBookLinkedList.Order memory sellOrder = sellOrderBook[market][token][price]
                .nodes[head_]
                .order;

            if (remainingAmount >= sellAmount) {
                // Full match
                uint256 matchedUsdc = (sellAmount * price) / 1e6;

                sellOrderBook[market][token][price].popHead();
                _sellOrders[market][token]._remove(sellOrder.seller, head_);
                _removePrice(market, token, price, true);

                tokenContract.safeTransfer(msg.sender, sellAmount);
                collateralToken.safeTransfer(sellOrder.seller, matchedUsdc);

                emit OrderMatched(market, token, sellOrder.seller, msg.sender, price, sellAmount);

                remainingAmount -= sellAmount;
            } else {
                // Partial match
                uint256 matchedUsdc = (remainingAmount * price) / 1e6;

                sellOrderBook[market][token][price].nodes[head_].order.amount -= remainingAmount;
                _sellOrders[market][token]._subVolume(sellOrder.seller, head_, remainingAmount);

                tokenContract.safeTransfer(msg.sender, remainingAmount);
                collateralToken.safeTransfer(sellOrder.seller, matchedUsdc);

                emit OrderMatched(market, token, sellOrder.seller, msg.sender, price, remainingAmount);

                remainingAmount = 0;
            }
        }
    }

    /// @notice Add price to sorted list
    function _addSellPrice(address market, address token, uint256 price) internal {
        if (!_activePrices[market][token][price]) {
            _activePrices[market][token][price] = true;
            _insertSorted(_sellPrices[market][token], price, true);
        }
    }

    function _addBuyPrice(address market, address token, uint256 price) internal {
        if (!_activePrices[market][token][price]) {
            _activePrices[market][token][price] = true;
            _insertSorted(_buyPrices[market][token], price, false);
        }
    }

    function _removePrice(address market, address token, uint256 price, bool isSell) internal {
        if (sellOrderBook[market][token][price].length == 0 && 
            buyOrderBook[market][token][price].length == 0) {
            _activePrices[market][token][price] = false;
            if (isSell) {
                _removeFromArray(_sellPrices[market][token], price);
            } else {
                _removeFromArray(_buyPrices[market][token], price);
            }
        }
    }

    function _insertSorted(uint256[] storage arr, uint256 price, bool ascending) internal {
        uint256 i = 0;
        while (i < arr.length && (ascending ? arr[i] < price : arr[i] > price)) {
            i++;
        }
        arr.push(0);
        for (uint256 j = arr.length - 1; j > i; j--) {
            arr[j] = arr[j - 1];
        }
        arr[i] = price;
    }

    function _removeFromArray(uint256[] storage arr, uint256 price) internal {
        for (uint256 i = 0; i < arr.length; i++) {
            if (arr[i] == price) {
                arr[i] = arr[arr.length - 1];
                arr.pop();
                return;
            }
        }
    }

    /// @notice Get best bid price
    function getBestBid(address market, address token) external view returns (uint256) {
        uint256[] storage prices = _buyPrices[market][token];
        if (prices.length == 0) return 0;
        for (uint256 i = 0; i < prices.length; i++) {
            if (buyOrderBook[market][token][prices[i]].length > 0) {
                return prices[i];
            }
        }
        return 0;
    }

    /// @notice Get best ask price
    function getBestAsk(address market, address token) external view returns (uint256) {
        uint256[] storage prices = _sellPrices[market][token];
        if (prices.length == 0) return 0;
        for (uint256 i = 0; i < prices.length; i++) {
            if (sellOrderBook[market][token][prices[i]].length > 0) {
                return prices[i];
            }
        }
        return 0;
    }

    /// @notice Collect fees
    function collectFees() external onlyOwner {
        uint256 fee = accumulatedFee;
        accumulatedFee = 0;
        collateralToken.safeTransfer(owner(), fee);
    }
}

